  }
}
@media screen and (max-width: 800px) {
  #wrapper {
    margin-right: 0;
  }
}
#wrapper #myCanvasContainer {
  margin-left: auto;
  margin-right: auto;
  width: 500px;
}
p {
  text-indent: 0em;
}
#header-fix {
